<?php
include 'Loader.php'; // 引入加载器
spl_autoload_register('Loader::autoload'); // 注册自动加载
use wycto\pay\PayFactory;

$config = array('apjs_src'=>'alipay/weixin/ap.js','jump_url'=>"alipay/weixin/pay.html");
$aliPay = PayFactory::getApp('alipay',$config,'weixin');

/*** 请填写以下配置信息 ***/
$appid = '2016091300504235';  //https://open.alipay.com 账户中心->密钥管理->开放平台密钥，填写添加了电脑网站支付的应用的APPID
$rsaPrivateKey="";		//商户私钥，填写对应签名算法类型的私钥，如何生成密钥参考：https://docs.open.alipay.com/291/105971和https://docs.open.alipay.com/200/105310
$signType = 'RSA2';			//签名算法类型，支持RSA2和RSA，推荐使用RSA2
$returnUrl = 'http://' . $_SERVER['HTTP_HOST'] . '/alipay/return.php';     //付款成功后的同步回调地址
$notifyUrl = 'http://' . $_SERVER['HTTP_HOST'] . '/alipay/notify.php';     //付款成功后的异步回调地址

